<?php
$version = "V1.3.5";

// ...
$longest_name = 4; // Defaults to 4 because of header "NAME"
$longest_ext = 3; // Defaults to 3 because of header "EXT"
$longest_size = 4; // Defaults to 4 because of header "SIZE"
$sum_files = 0;
$sum_sizes = 0;
$row_padding_between = '  ';
$lowest_allowed_directory = '/nas';

// set directory offset
$working_dir = './';
if (isset($_GET['wd'])) {
    $working_dir = $_GET['wd'];
}

// Deny and redirect file system requests below $lowest_allowed_directory
if ($working_dir[0] === '/') {
    die('Nice try, buddy.');
}
if (substr(realpath($working_dir), 0, strlen($lowest_allowed_directory)) !== $lowest_allowed_directory) {
    $working_dir = './';
}

// Initial directory scan
$nodeInfoArray = [];
$nodeInfoArrayDirectories = [];
$nodeInfoArrayFiles = [];
$subDirectories = [];
foreach (new DirectoryIterator($working_dir) as $nodeInfo) {
    $node = [
            'name' => $nodeInfo->getBasename(),
            'ext' => $nodeInfo->getExtension(),
            'size' => $nodeInfo->getSize(),
            'time' => $nodeInfo->getMTime(),
            'link' => $working_dir . $nodeInfo->getBasename()
    ];

    // Skip single dot directory
    if ($nodeInfo->isDot() && $nodeInfo->getBasename() === '.') {
        continue;
    }

    // Skip useless mac DS_STORE files
    if (stripos($nodeInfo->getBasename(), 'DS_Store') !== false) {
	    continue;
    }

    // Skip index.php file
    if (stripos($nodeInfo->getBasename(), 'index.php') !== false) {
            continue;
    }

    // For files without extensions just use a dash
    if ($nodeInfo->getExtension() === '') {
        $node['ext'] = '-';
    }

    // For files with extensions, remove the extension from display name
    if ($nodeInfo->getExtension() !== '' && !$nodeInfo->isDir()) {
        $node['name'] = substr($node['name'], 0, strlen($node['name']) - strlen($node['ext']) - 1);
    }


    // For directories set special DIR type
    if ($nodeInfo->isDir()) {
        $node['ext'] = 'DIR';
        $node['link'] = '?wd=' . $working_dir . urlencode($nodeInfo->getBasename()) . '/';
    }

    // For LINK files set special link
    if (strtoupper($nodeInfo->getExtension()) === 'LINK') {
        $node['link'] = trim(file_get_contents($working_dir . $nodeInfo->getBasename()));
        // Prevent some very basic XSS
        if (stripos($node['link'], '"') !== false || stripos($node['link'], '<script') !== false) {
            $node['link'] = '#';
        }
    }

    // Get biggest lengths
    if (strlen($nodeInfo->getBasename()) > $longest_name) {
        $longest_name = strlen($nodeInfo->getBasename());
    }
    if (strlen($nodeInfo->getExtension()) > $longest_ext) {
        $longest_ext = strlen($nodeInfo->getExtension());
    }
    if (strlen($nodeInfo->getSize()) > $longest_size) {
        $longest_size = strlen($nodeInfo->getSize());
    }

    // Sum up file count and sizes
    if (!$nodeInfo->isDot()) {
        $sum_files++;
        $sum_sizes += $nodeInfo->getSize();
    }

    if ($nodeInfo->isDir()) {
        $nodeInfoArrayDirectories[] = $node;
    } else {
        $nodeInfoArrayFiles[] = $node;
    }
}

// Sort all rows alphabetically
sort($nodeInfoArrayDirectories);
sort($nodeInfoArrayFiles);

$nodeInfoArray = array_merge($nodeInfoArrayDirectories, $nodeInfoArrayFiles);

// Generate formatted data rows
$formatted_rows = [];
foreach ($nodeInfoArray as $node) {
    $row = '';

    // Name
    $padding = getPaddingSpaces($longest_name - strlen($node['name']));
    $row .= $node['name'] . $padding . $row_padding_between;

    // Ext
    $padding = getPaddingSpaces($longest_ext - strlen($node['ext']));
    $row .= $node['ext'] . $padding . $row_padding_between;

    // Size
    $padding = getPaddingSpaces($longest_size - strlen($node['size']));
    $row .= $node['size'] . $padding . $row_padding_between;

    // Modified
    $row .= date('H:i d M', $node['time']);

    $formatted_rows[] = [
            'row' => strtoupper($row),
            'link' => $node['link']
    ];
}

// Generate formatted header row
$header = 'NAME' . getPaddingSpaces($longest_name - 4) . $row_padding_between . 'EXT' . getPaddingSpaces($longest_ext - 3) . $row_padding_between . 'SIZE' . getPaddingSpaces($longest_size - 4) . $row_padding_between . 'MODIFIED';

// Helper function
function getPaddingSpaces($count) {
    $spaces = '';
    for ($i = 0; $i < $count; $i++) {
        $spaces .= ' ';
    }

    return $spaces;
}

?>
